package bucles;

public class Repaso {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//En Java tenemos 4 tipos de bucles:
		// do{ intrucciones } while(condicion);
		//for(inicialización;condición;modificación){ instrucciones }
		//Ejemplo: Programa que muestra por pantalla los números pares del 2 al 20
		for(int i = 0;i<=40;i=i+2) {
			System.out.print(i + " ");
		}
		System.out.println();
		
		//while (condicion) {instrucciones};
		
		int j = 30;
		while(j<=40) {
			System.out.print(j + " ");
			j+=2;
			//j= j+2;
		}
		
		//Ejercicio 1: Calcular la suma de todos los números que están
		//dentro de un intervalo n , m , siendo n y m valores introducidos por teclado
		
		
		
	}

}
